// ChildRegistrationForm.js
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Registration.css'

const ChildRegistrationForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    age: '',
    gender: 'male',
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Validate form data
    if (!formData.firstName || !formData.lastName || !formData.age) {
      alert('Please fill in all mandatory fields.');
      return;
    }

    // Save child to local storage
    const newChild = {
      id: Date.now(),
      ...formData,
    };

    const storedChildren = JSON.parse(localStorage.getItem('children')) || [];
    localStorage.setItem('children', JSON.stringify([...storedChildren, newChild]));

    // Clear the form
    setFormData({
      firstName: '',
      lastName: '',
      age: '',
      gender: 'male',
    });

    // Navigate to the children list page
    navigate('/children-list');
  };

  return (



    <div className='container'>


      <h1>Child Registration Form</h1>
      <form className='form' onSubmit={handleFormSubmit}>
        <label>
          First Name:
          <input className='input-box' type="text" name="firstName" value={formData.firstName} onChange={handleInputChange} required />
        </label>
        <label>
          Last Name:
          <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} required />
        </label>
        <label>
          Age:
          <input type="text" name="age" value={formData.age} onChange={handleInputChange} required />
        </label>
        <label>
          Gender:
          <select name="gender" value={formData.gender} onChange={handleInputChange}>
            <option value="male">Male</option>
            <option value="female">Female</option>
          </select>
        </label>
        <button type="submit" id='summit-buton'>Register Child</button>
      </form>


    </div>
  );
};

export default ChildRegistrationForm;
