// ChildRegistrationApp.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ChildRegistrationApp = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    gender: 'male',
    age: '',
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Validate age (you can add more validations as needed)
    if (!formData.age || isNaN(formData.age)) {
      alert('Please enter a valid age.');
      return;
    }

    // Add the new child to local storage
    const newChild = {
      id: Date.now(),
      ...formData,
    };

    const storedChildList = JSON.parse(localStorage.getItem('childList')) || [];
    localStorage.setItem('childList', JSON.stringify([...storedChildList, newChild]));

    // Clear the form
    setFormData({
      firstName: '',
      lastName: '',
      gender: 'male',
      age: '',
    });
  };

  const handleSeeSavedChildren = () => {
    navigate('/child-list');
  };

  return (
    <div>
      <h1>Child Registration App</h1>

      {/* Child Registration Form */}
      <form onSubmit={handleFormSubmit}>
        {/* ... (your form fields) */}
        <button type="submit">Register Child</button>
      </form>

      {/* Button to See Saved Children */}
      <button onClick={handleSeeSavedChildren}>See Saved Children</button>
    </div>
  );
};

export default ChildRegistrationApp;
